@import url('https://fonts.googleapis.com/css2?family=Nunito+Sans:ital,wght@1,200&display=swap');


*{
  padding: 0;
  margin: 0;
  box-sizing: border-box;
  font-family: 'Nunito Sans', sans-serif;

}

.navbar{
  position: fixed;
  left: 0;
  right: 0;
  width: 100%;
  z-index: 10;
  height: .3rem;

  .menu{
    max-width: 80rem;
    width: 100%;
    margin: 0 auto;
    padding: 0 3rem;
    display: flex;
    justify-content: space-between;
    align-items: center;
    color: white;

    .logo{
      font-size: 2rem;
      font-weight: 1000;
      text-transform: uppercase;
      letter-spacing: 2px;
      line-height: 5rem;
      color: white;

      span{
        font-size: 2rem;

        font-weight: bold;
        font-family: inherit;
      }


    }

  }
  .burger{
    height: 5rem;
    width: 3rem;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: flex-end;
    z-index: 10;
    .bar{
      width: 2rem;
      height: 2px;
      border-radius: 2px;
      background-color: aliceblue;
      transition: .3s;
      position: relative;
      &:after, &:before{
        content: "";
        position: absolute;
        width: inherit;
        height: inherit;
        background-color: white;
        transition: 0.5s;
      }
      &:after{
        transform: translateY(9px);

      }
      &:before{
        transform: translateY(-9px);
      }
    }


  }
}
