        }&.header_five{
           background: url("./assets/1614406622_33-p-temnii-fon-dlya-fotoshopa-stena-36.jpg") no-repeat ;
         }
      .header{
        position: absolute;
        width: 100%;
        top: 0;
        height: 100%;
        left: 0;
        //background-color: ;
        display: flex;
        justify-content: center;
        align-items: center;
        &_item{
          max-width: 35rem;
          text-align: center;
          color: white;
          padding: 0 2rem;
          .title{
            font-size: 2rem;
          }
          //button
