.wrap {
  width: 500px;
}

$height: 46px;

.select {
  transform: translateY(-15px);
  width: 100%;
  position: relative;
  z-index: 100;
  color: white;
  background-color: black;
  opacity: 0.5;


  &.open {
    .select__dropdown {
      display: block;
      background-color: black;
      opacity: 1;
      color: white;
    }

    .select__input {
      border-bottom: none;
      color: white;
    }

    .select__backdrop {
      display: block;
    }
  }

  &__input {
    border: 1px solid #ccc;
    border-radius: 5px;
    height: $height;
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 0 1rem;
    cursor: pointer;
  }

  &__dropdown {
    display: none;
    position: absolute;
    border: 1px solid #ccc;
    border-radius: 5px;
    top: $height;
    left: 0;
    right: 0;
    max-height: 200px;
    overflow-y: auto;
    box-shadow: 2px 3px 5px rgba(0,0,0,.3);
  }

  &__list {
    padding: 0;
    margin: 0;
    list-style: none;
  }

  &__backdrop {
    position: fixed;
    display: none;
    z-index: 100;
    width: 100%;
    height: 100%;
    top: 0;
    right: 0;
    left: 0;
    bottom: 0;
    background: transparent;
  }

  &__item {
    padding: 1rem;
    border-bottom: 1px solid #ccc;

    &.selected {
      background-color: darkcyan;
    }

    &:hover {
      background-color: darkslategrey;
      cursor: pointer;
      transition: .15s background-color ease-in;
    }
  }

}





























.container.active{
  .bar{
    transform: rotate(360deg);
    background-color: transparent;
    &:before{
      transform: translateY(0) rotate(50deg);
    }
    &:after{
      transform: translateY(0) rotate(-50deg);
    }

  }
  .main{
    animation: main-animation 1s ease;
    cursor: pointer;
    transform:rotateY(20deg) translateZ(300px) scale(0.5);
    &:hover{
      transform: rotateY(20deg) translateZ(340px) scale(0.5);
      + .shadow.one{
        transform:  rotateY(40deg) translateZ(230px) scale(0.5);
      }}
  }
  //.shadow.one{
